package ejercicio3;

public class Ejercicio3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 10;
		int y = 5;
		double n = 2.5;
		double m = 1.5;
		
		System.out.println("Variable X = " + x);
		System.out.println("Variable Y = " + y);
		System.out.println("Variable N = " + n);
		System.out.println("Variable N = " + m);
		
		System.out.println("La Suma de X + Y = " + (x+y));
		System.out.println("La Difrencia de X - Y = " + (x-y));
		System.out.println("El producto de X * Y = " + (x*y));
		System.out.println("El cociente X / Y = " + (x/y));
		System.out.println("El resto X % Y = " + (x%y));
		
		System.out.println("La Suma de N + M = " + (n+m));
		System.out.println("La Difrencia de N - M = " + (n-m));
		System.out.println("El producto de N * M = " + (n*m));
		System.out.println("El cociente N / M = " + (n/m));
		System.out.println("El resto N % M " + (n%m));
		
		System.out.println("La Suma de X + N = " + (x+n));
		System.out.println("El cociente Y / M = " + (y+m));
		System.out.println("El resto Y % M = " + (y+m));
		
		System.out.println("El doble de X = " + x*2);
		System.out.println("El doble de Y = " + y*2);
		System.out.println("El doble de N = " + n*2);
		System.out.println("El doble de M = " + m*2);
		System.out.println("La Suma de todas las variables es = " + (x+y+n+m));
		System.out.println("El producto de todas las variables es = " + x*y*n*m);









	}

}
