package ejercicio4;

public class Ejercicio4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int n = 5;
		
		System.out.println("Valor inicial: " + n);
		n = n + 77;
		System.out.println("N + 77 = " + n);
		n = n - 3;
		System.out.println("N - 3 = " + n);
		n = n * 2;
		System.out.println("N * 2 = " + n);

	}

}
